import React, { useState } from "react";
import { useHistory } from "react-router-dom";


export default function Books() {
    const history = useHistory();
  const [data, setData] = useState([
    { id: 1, name: "Book 1" ,imgUrl : "https://exploreyork.org.uk/wp-content/uploads/2021/06/Books-composite-1200x817.jpg" , discreption: "Have you had a cherished dream one day and you are still striving to achieve it? Do you want to focus on your goals and dreams and not lose hope in achieving them? Do you feel that time is not enough for you and that you suffer from stress or loneliness? Discover new techniques that help you focus and achieve your goals. This book is full of tips and aphorisms to help you achieve your dreams." ,Writer: "hal Fisher"},
    { id: 2, name: "Book 2" },
  ]);

  const goToCard  =(id)=>{
    history.push(`/book/${id}`);
  }

  return (
    <div>
      {data.map((ele) => {
        return (
          <div onClick={()=>{goToCard(ele.id)}}>
            <h1>{ele.name}</h1>
            <img src={ele.imgUrl}/>
            <p>{ele.discreption}</p>
          </div>
        );
      })}
    </div>
  );
}
